contract = new web3.eth.Contract(abi, contractAddress);


web3.eth.getAccounts(function(err, accounts) {
  if (err != null) {
    alert("Error retrieving accounts.");
    return;
  }
  if (accounts.length == 0) {
    alert("No account found! Make sure the Ethereum client is configured properly.");
    return;
  }
  account = accounts[0];
  contract.methods.balanceOf (account).call( {from: account}).then( function(bl) { 


  console.log('Account: ' + account);
  web3.eth.defaultAccount = account;
  totalToken=bl;
    console.log("Total Token: ", bl); 
   
  });

});

//Smart contract functions
function registerSetInfo() {
   
  info = $("#newInfo").val();

  contract.methods.mint(info).estimateGas({gas: 5000000}, function(error, gasAmount){
      console.log(gasAmount);
    if(gasAmount == 5000000)
        console.log('Method ran out of gas'+gasAmount);
});

  contract.methods.mint (info).send( {from: account, gas:3000000}).then( function(tx) { 
    console.log("Transaction: ", tx); 
  });
  $("#newInfo").val('');
}


async function registerGetInfo() {
  var j = "<ul>"
  for (let i = 0; i < totalToken; i++) {
      const response = await contract.methods.token(i).call();
      console.log(response);
 j+="<li>"+ response +"</li>"
 
  }
j+="</ul>"
document.getElementById('lastInfo').innerHTML = j;
}

